mapper.select:
SELECT * FROM(
SELECT t2.id id,t2.user_id userId,t1.extend_id
extendId,t2.value value,
t1.extend_code extendCode FROM (
SELECT id
extend_id,code extend_code FROM attribute_extend
WHERE 1=1

	AND `code` LIKE #{extendCode, jdbcType=VARCHAR}

AND tenancy_id = #{tenancyId, jdbcType=BIGINT}
) t1 LEFT JOIN (
SELECT
id,user_id,value,extend_id FROM user_extend_val
WHERE user_id=#{userId}
AND tenancy_id = #{tenancyId, jdbcType=BIGINT}
)t2 ON
t1.extend_id=t2.extend_id
) tt1
WHERE 1 = 1

	AND userId is not NULL

ORDER BY userId DESC,id DESC

	limit ${startIndex}, ${pageSize}

SELECT count(id) extend_description FROM attribute_extend WHERE 1=1

	AND `code` LIKE #{extendCode, jdbcType=VARCHAR}

AND tenancy_id = #{tenancyId, jdbcType=BIGINT}

select count(id) from user_extend_val t1 where t1.user_id = #{userId}
and tenancy_id = #{tenancyId, jdbcType=BIGINT}
and exists (
select id
from attribute_extend t2 where t2.id = t1.extend_id

	AND `code` LIKE #{extendCode, jdbcType=VARCHAR}

AND tenancy_id = #{tenancyId, jdbcType=BIGINT}
)

mapper.update:
UPDATE user_extend_val SET value = #{value, jdbcType=VARCHAR} where user_id
= #{userId, jdbcType=BIGINT} and extend_id = #{extendId,
jdbcType=BIGINT}
